<template>\n  <header>\n    <div class=\"container\">\n      <div class=\"header\">\n        <nuxt-link to=\"/\" class=\"header-logo\">\n          <img src=\"~/assets/img/logo.svg\" alt=\"Fortnite\">\n        </nuxt-link>\n        <a class=\"header-btn\" @click=\"scrollToRoulette\">\n          Spin Now\n        </a>\n      </div>\n    </div>\n  </header>\n</template>\n\n<script>\nexport default {\n  name: 'TheHeader',\n  methods: {\n    scrollToRoulette() {\n      const rouletteEl = document.querySelector('.roulette-bl');\n      if (rouletteEl) {\n        rouletteEl.scrollIntoView({ behavior: 'smooth' });\n      }\n    }\n  }\n}\n</script>