<template>\n  <div class=\"login-container\">\n    <div class=\"login-form\">\n      <img src=\"~/assets/img/logo.svg\" alt=\"Fortnite Logo\" class=\"login-logo\">\n      <h1 class=\"login-title\">Sign In</h1>\n      <p class=\"login-desc\">Sign in to claim your V-Bucks prize</p>\n      \n      <form @submit.prevent=\"handleLogin\">\n        <div class=\"form-group\">\n          <label for=\"email\">Email</label>\n          <input type=\"email\" id=\"email\" v-model=\"email\" required placeholder=\"Enter your email\">\n        </div>\n        \n        <div class=\"form-group\">\n          <label for=\"password\">Password</label>\n          <input type=\"password\" id=\"password\" v-model=\"password\" required placeholder=\"Enter your password\">\n        </div>\n        \n        <button type=\"submit\" class=\"login-btn\">SIGN IN</button>\n      </form>\n      \n      <div class=\"login-footer\">\n        <p>Don't have an account? <a href=\"#\" @click.prevent=\"goToRegister\">Sign up</a></p>\n      </div>\n    </div>\n  </div>\n</template>\n\n<script>\nexport default {\n  name: 'LoginPage',\n  layout: 'default',\n  data() {\n    return {\n      email: '',\n      password: ''\n    }\n  },\n  head() {\n    return {\n      title: 'Sign In - FORTNITE V-BUCKS GIVEAWAY'\n    }\n  },\n  methods: {\n    handleLogin() {\n      // In a real app you would authenticate with a backend\n      // For this demo, we're just showing a placeholder\n      alert('Authentication would happen here in a real app')\n      \n      // Redirect to a success page or back to homepage\n      this.$router.push('/')\n    },\n    goToRegister() {\n      // For demo purposes just showing an alert\n      alert('Registration page would be here in a real app')\n    }\n  }\n}\n</script>\n\n<style lang=\"scss\" scoped>\n.login-container {\n  display: flex;\n  justify-content: center;\n  align-items: center;\n  min-height: calc(100vh - 200px);\n  padding: 20px;\n}\n\n.login-form {\n  background-color: #15161d;\n  padding: 40px;\n  border-radius: 15px;\n  width: 100%;\n  max-width: 500px;\n  box-shadow: 0 0 20px rgba(0, 0, 0, 0.3);\n}\n\n.login-logo {\n  display: block;\n  margin: 0 auto 20px;\n  width: 120px;\n}\n\n.login-title {\n  color: $light-title;\n  text-align: center;\n  font-size: 32px;\n  margin-bottom: 10px;\n  font-family: 'HeadingNowItalic';\n}\n\n.login-desc {\n  color: $light-text-secondary;\n  text-align: center;\n  margin-bottom: 30px;\n  font-size: 16px;\n}\n\n.form-group {\n  margin-bottom: 20px;\n  \n  label {\n    display: block;\n    color: $light-title;\n    margin-bottom: 5px;\n    font-size: 16px;\n  }\n  \n  input {\n    width: 100%;\n    padding: 12px;\n    background: rgba(255, 255, 255, 0.1);\n    border: 1px solid rgba(255, 255, 255, 0.2);\n    border-radius: 8px;\n    color: $light-title;\n    font-size: 16px;\n    transition: border-color 0.3s;\n    \n    &:focus {\n      outline: none;\n      border-color: $yellow-accent;\n    }\n    \n    &::placeholder {\n      color: rgba(255, 255, 255, 0.5);\n    }\n  }\n}\n\n.login-btn {\n  display: block;\n  width: 100%;\n  background-color: $yellow-accent;\n  color: $yellow-dark;\n  border: none;\n  padding: 14px;\n  font-size: 18px;\n  font-weight: bold;\n  border-radius: 8px;\n  cursor: pointer;\n  transition: transform 0.3s;\n  margin-top: 30px;\n  text-transform: uppercase;\n  \n  &:hover {\n    transform: scale(1.03);\n  }\n}\n\n.login-footer {\n  margin-top: 30px;\n  text-align: center;\n  color: $light-text-secondary;\n  font-size: 16px;\n  \n  a {\n    color: $yellow-accent;\n    text-decoration: none;\n    transition: opacity 0.3s;\n    \n    &:hover {\n      opacity: 0.8;\n    }\n  }\n}\n\n@media (max-width: 600px) {\n  .login-form {\n    padding: 30px 20px;\n  }\n  \n  .login-title {\n    font-size: 28px;\n  }\n  \n  .login-desc {\n    font-size: 14px;\n  }\n}\n</style>